import { useRef, useLayoutEffect, useCallback, useState} from "react"
import {useTransform, useSpring, motion, useScroll} from 'framer-motion'
import logo from './aiu.png'

const SmoothScroll = () => {
    const scrollRef = useRef(null)
    const ghostRef = useRef(null)
    const [scrollRange, setScrollRange] = useState(0)
    const [viewportW, setViewportW] = useState(0)
  
    useLayoutEffect(() => {
      scrollRef && setScrollRange(scrollRef.current.scrollWidth)
    }, [scrollRef])
  
    const onResize = useCallback(entries => {
      for (let entry of entries) {
        setViewportW(entry.contentRect.width)
      }
    }, [])
  
    useLayoutEffect(() => {
      const resizeObserver = new ResizeObserver(entries => onResize(entries))
      resizeObserver.observe(ghostRef.current)
      return () => resizeObserver.disconnect()
    }, [onResize])
  
    const { scrollYProgress } = useScroll();
    const transform = useTransform(
      scrollYProgress,
      [0, 1],
      [0, -scrollRange + viewportW]
    )
    const physics = { damping: 15, mass: 0.27, stiffness: 55 }
    const spring = useSpring(transform, physics)
  
    return (
      <>
        <div className="scroll-container">
          <motion.section
            ref={scrollRef}
            style={{ x: spring }}
            className="thumbnails-container"
          >
            <div className="thumbnails">
              
              <motion.h3 className='ldd'>LOCKDOWNDIARIES</motion.h3>
      <h4>presents</h4>

      <img src={logo} alt='cover' className='cover'></img>
       
        <button class="button-56"  >Check out!</button>
        <motion.h3 className='ldd'>LOCKDOWNDIARIES</motion.h3>
      <h4>presents</h4>

      <img src={logo} alt='cover' className='cover'></img>
       
        <button class="button-56" >Check out!</button>
        <motion.h3 className='ldd'>LOCKDOWNDIARIES</motion.h3>
      <h4>presents</h4>

      <img src={logo} alt='cover' className='cover'></img>
       
        <button class="button-56" >Check out!</button>
        <motion.h3 className='ldd'>LOCKDOWNDIARIES</motion.h3>
      <h4>presents</h4>

      <img src={logo} alt='cover' className='cover'></img>
       
        <button class="button-56"  >Check out!</button>
        <motion.h3 className='ldd'>LOCKDOWNDIARIES</motion.h3>
      <h4>presents</h4>

      <img src={logo} alt='cover' className='cover'></img>
       
        <button class="button-56"  >Check out!</button>
        
      
            </div>
          </motion.section>
        </div>
        <div ref={ghostRef} style={{ height: scrollRange }} className="ghost" />
      </>
    )
  }
  
  export default SmoothScroll
