import logo from './aiu.png';
import './App.css';
import {motion, useTransform, useScroll} from 'framer-motion'
import { useRef } from 'react';


function App() {
  async function handleCheckout (){
    fetch("https://vachana-backend.vercel.app/guests").then((data)=> console.log(data))
  }

  const horizontalscroll= ()=> {
    const targetRef= useRef<HTMLDivElement | null>(null);

    const {scrollYProgress } = useScroll({
      target: targetRef
    });
    const x = useTransform(scrollYProgress, [0, 1], ['1%', '-96%'])
    return (<section>
      <motion.div style={x} className="App">
      <motion.h3 className='ldd'>LOCKDOWNDIARIES</motion.h3>
      <h4>presents</h4>

      <img src={logo} alt='cover' className='cover'></img>

        <button class="button-56" onClick={handleCheckout} >Check out!</button>


    </motion.div>

    </section>)
  }
  return (
    <div>
      {horizontalscroll}
    </div>
  );
}

export default App;
